mapper.select:
select count(sid) from students s,Users u

    u.Uid=s.ConsultantId

        and sname like "%"#{sname}"%"


        and Smtel like "%"#{Smtel}"%"


        and u.LoginName=#{zixunshi}


        and s.QQ like "%"#{QQ}"%"


		and CreateData > #{StartData}


		and CreateData < #{EndData}


        and IsEffective=#{IsEffective}


        and IsPay=#{IsPay}


        and IsReturnVisit=#{IsReturnVisit}

select s.Sid, s.Sname, s.Sex, s.Age, s.Smtel, s.Education, s.State, s.Channel, s.Website, s.KeyWord, s.Department, s.NameConsultation, s.Region, s.IsReport, s.Curriculum, s.IsEffective, s.Scoring, s.IsReturnVisit, s.ReturnVisitData, s.Door, s.DoorData, s.Reason, s.IsPay, s.PayData, s.Money, s.IsRefund, s.IsClassEntry, s.ClassEntryData, s.ClassEntryRemarks, s.ReasonsRefund, s.EarnestMoney, s.EarnestMoneyData, s.Follow, s.QQ, s.WeiXin, s.IsEnroll, s.Remarks, s.ConsultantId, s.NetworkConsultantId, s.EntryPersonId, s.ConsultantRemarks, s.Int0,u.LoginName as zixunshi,s.String0 from students s,Users u
u.Uid=s.ConsultantId
and sname like "%"#{sname}"%"
and s.Smtel like "%"#{Smtel}"%"
and u.LoginName=#{zixunshi}
and s.QQ like "%"#{QQ}"%"
and ReturnVisitData > #{StartData}
and ReturnVisitData < #{EndData}
and IsEffective=#{IsEffective}
and IsPay=#{IsPay}
and IsReturnVisit=#{IsReturnVisit}
limit #{page},#{pageSize}

