		and s.QQ like "%"#{QQ}"%"


		and CreateData > #{StartData}


		and CreateData < #{EndData}


		and IsEffective=#{IsEffective}


		and IsPay=#{IsPay}


		and IsReturnVisit=#{IsReturnVisit}

select s.Sid, s.Sname, s.Sex, s.Age, s.Smtel, s.Education, s.State, s.Channel, s.Website, s.KeyWord, s.Department, s.NameConsultation, s.Region, s.IsReport, s.Curriculum, s.IsEffective, s.Scoring, s.IsReturnVisit, s.ReturnVisitData, s.Door, s.DoorData, s.Reason, s.IsPay, s.PayData, s.Money, s.IsRefund, s.IsClassEntry, s.ClassEntryData, s.ClassEntryRemarks, s.ReasonsRefund, s.EarnestMoney, s.EarnestMoneyData, s.Follow, s.QQ, s.WeiXin, s.IsEnroll, s.Remarks, s.ConsultantId, s.NetworkConsultantId, (select LoginName from Users where Uid=s.EntryPersonId) as lururen, s.ConsultantRemarks, s.Int0,u.LoginName as zixunshi,s.String0 from students s,Users u
s.ConsultantId=#{JiaoXueid} and u.Uid=s.ConsultantId
and sname like "%"#{sname}"%"
and s.Smtel like "%"#{Smtel}"%"
and u.LoginName=#{zixunshi}
and s.QQ like "%"#{QQ}"%"
and CreateData > #{StartData}
and CreateData < #{EndData}
and IsEffective=#{IsEffective}
and IsPay=#{IsPay}
and IsReturnVisit=#{IsReturnVisit}
limit #{page},#{pageSize}

select Tid,Studentid,UserId,TrackStartData,TrackEndData,ReturnVisit,TrackingMode,Content,NextTrackingData,Operation,s.Sname as StudentName,u.LoginName as UsersName from track t,Students s,Users u
t.Studentid=#{sid} and t.Studentid=s.Sid and t.UserId=u.Uid
limit #{page},#{pageSize}

