mapper.update:
update users set
IsLoginData=1
where Uid=#{id}

update users set
IsLoginData=0
where Uid=#{id}

mapper.select:
select Uid,LoginName,SignIn,SignInData from users

	IsLoginData=0

		and LoginName like "%"#{LoginName}"%"


		and SignIn=#{signIn}


		and SignInData BETWEEN #{cuanjiankaishi} AND #{cuanjianjieshu}


limit #{page},#{pageSize}

select count(Uid) from users

	IsLoginData=0

		and LoginName like "%"#{LoginName}"%"


		and SignIn=#{signIn}


		and SignInData BETWEEN #{cuanjiankaishi} AND #{cuanjianjieshu}
